mapper.select:
SELECT 
FROM t_content_chapter
WHERE content_id = #{contentId}
ORDER BY chapter_num ASC
LIMIT #{offset}, #{limit}

SELECT 
FROM t_content_chapter
WHERE content_id = #{contentId} AND chapter_num = #{chapterNum}

SELECT 
FROM t_content_chapter
WHERE content_id = #{contentId} 
AND word_count BETWEEN #{minWordCount} AND #{maxWordCount}
ORDER BY word_count DESC

mapper.update:
UPDATE t_content_chapter
SET status = #{status}
WHERE id IN

    #{id}

mapper.select:
SELECT COUNT(*)
FROM t_content_chapter
WHERE content_id = #{contentId}

mapper.update:
UPDATE t_content_chapter t1
SET chapter_num = (
    SELECT COUNT(*) + 1
    FROM (SELECT * FROM t_content_chapter) t2
    WHERE t2.content_id = #{contentId}
    AND t2.create_time < t1.create_time
)
WHERE content_id = #{contentId}
