mapper.select:
SELECT 
    id, follower_user_id, followed_user_id, follow_type, 
    status, created_time, updated_time
FROM t_follow 
WHERE follower_user_id = #{followerUserId} 
  AND followed_user_id = #{followedUserId}
  AND status = #{status}
LIMIT 1

SELECT 
    f1.id, f1.follower_user_id, f1.followed_user_id, f1.follow_type,
    f1.status, f1.created_time, f1.updated_time
FROM t_follow f1
INNER JOIN t_follow f2 ON f1.follower_user_id = f2.followed_user_id 
                        AND f1.followed_user_id = f2.follower_user_id
WHERE f1.follower_user_id = #{userId}
  AND f1.status = #{status}
  AND f2.status = #{status}
ORDER BY f1.created_time DESC

