mapper.select:
SELECT 
FROM t_user_interest_tag
WHERE user_id = #{userId}

    AND status = #{status}

ORDER BY interest_score DESC, create_time DESC

SELECT 
FROM t_user_interest_tag
WHERE tag_id = #{tagId}

    AND status = #{status}

ORDER BY interest_score DESC, create_time DESC

    LIMIT #{limit}

mapper.delete:
DELETE FROM t_user_interest_tag
WHERE user_id = #{userId}
AND tag_id IN

    #{tagId}

mapper.select:
SELECT 
FROM t_user_interest_tag
WHERE user_id = #{userId}
AND status = 'active'
AND interest_score >= #{minScore}
ORDER BY interest_score DESC, create_time DESC

    LIMIT #{limit}
