mapper.select:
SELECT * FROM t_task_template 
WHERE task_action = #{taskAction}

    AND is_active = #{isActive}


    AND (start_date IS NULL OR start_date <= #{currentDate})
    AND (end_date IS NULL OR end_date >= #{currentDate})

ORDER BY sort_order ASC

SELECT * FROM t_task_template 


        AND task_name LIKE CONCAT('%', #{taskName}, '%')


        AND task_type = #{taskType}


        AND task_category = #{taskCategory}


        AND task_action = #{taskAction}


        AND is_active = #{isActive}


        AND (start_date IS NULL OR start_date >= #{startDate})


        AND (end_date IS NULL OR end_date <= #{endDate})




        ORDER BY ${orderBy} ${orderDirection}


        ORDER BY sort_order ASC

SELECT * FROM t_task_template 
WHERE task_category = #{taskCategory}

    AND is_active = #{isActive}

ORDER BY sort_order ASC

SELECT task_type, COUNT(*) as count FROM t_task_template
WHERE is_active = #{isActive}
GROUP BY task_type

SELECT task_category, COUNT(*) as count FROM t_task_template
WHERE is_active = #{isActive}
GROUP BY task_category

update.foreach:
UPDATE t_task_template 
SET sort_order = #{item.sortOrder}, update_time = NOW()
WHERE id = #{item.id}

mapper.select:
SELECT * FROM t_task_template 
WHERE sort_order BETWEEN #{minOrder} AND #{maxOrder}

    AND is_active = #{isActive}

ORDER BY sort_order ASC

SELECT * FROM t_task_template 


        AND (task_name LIKE CONCAT('%', #{keyword}, '%') 
             OR task_desc LIKE CONCAT('%', #{keyword}, '%'))


        AND task_type = #{taskType}


        AND task_category = #{taskCategory}


        AND is_active = #{isActive}


ORDER BY sort_order ASC

SELECT COUNT(*) > 0 FROM t_task_template 
WHERE task_name = #{taskName}

    AND id != #{excludeId}

SELECT COUNT(*) > 0 FROM t_task_template 
WHERE task_action = #{taskAction}

    AND is_active = #{isActive}
