mapper.select:
SELECT 
FROM t_user

    status != 'deleted'

        AND username LIKE CONCAT('%', #{username}, '%')


        AND email = #{email}


        AND phone = #{phone}


        AND status = #{status}


        AND inviter_id = #{inviterId}


ORDER BY create_time DESC
LIMIT #{size} OFFSET #{offset}

SELECT COUNT(*)
FROM t_user

    status != 'deleted'

        AND username LIKE CONCAT('%', #{username}, '%')


        AND email = #{email}


        AND phone = #{phone}


        AND status = #{status}


        AND inviter_id = #{inviterId}

SELECT FROM t_user WHERE id IN
#{id}
AND status != 'deleted' ORDER BY create_time DESC

mapper.insert:
INSERT INTO t_user (
    username, email, phone, password_hash, status, invite_code, inviter_id, create_time, update_time
) VALUES (
    #{username}, #{email}, #{phone}, #{passwordHash}, #{status}, #{inviteCode}, #{inviterId}, NOW(), NOW()
)

mapper.update:
UPDATE t_user
username = #{username}, email = #{email}, phone = #{phone}, password_hash = #{passwordHash}, status = #{status}, invite_code = #{inviteCode}, inviter_id = #{inviterId}, update_time = NOW()
WHERE id = #{id}

UPDATE t_user
SET status = #{status}, update_time = NOW()
WHERE id = #{userId}

UPDATE t_user
SET password_hash = #{passwordHash}, update_time = NOW()
WHERE id = #{userId}

UPDATE t_user
SET status = 'deleted', update_time = NOW()
WHERE id = #{userId}
